import React, { useState } from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  Slider,
  FormControlLabel,
  Checkbox,
  Radio,
  RadioGroup,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  TextField,
  Chip
} from '@mui/material';

interface Video {
  title: string;
  publishedAt: string;
  url: string;
  videoId: string;
  thumbnail: string;
  statistics: {
    viewCount: string;
    likeCount: string;
    commentCount: string;
  };
  performance?: {
    views: string;
    likes: string;
    comments: string;
  };
}

interface ProductSelectionProps {
  videos: Video[];
  onProductSelect: (selection: any) => void;
}

const packages = [
  { name: 'BASIC 패키지', price: 100000 },
  { name: 'STANDARD 패키지', price: 200000 },
  { name: 'PREMIUM 패키지', price: 300000 },
  { name: 'VIP 패키지', price: 500000 }
];

export const ProductSelection: React.FC<ProductSelectionProps> = ({ videos = [], onProductSelect }) => {
  const [selectedVideos, setSelectedVideos] = useState<{[key: string]: boolean}>({});
  const [subscriberCount, setSubscriberCount] = useState<number>(0);
  const [selectedPackage, setSelectedPackage] = useState<string>('');
  const [metrics, setMetrics] = useState<{[key: string]: {
    views: number;
    likes: number;
    comments: number;
  }}>({});

  const handleVideoSelect = (videoUrl: string) => {
    setSelectedVideos(prev => ({
      ...prev,
      [videoUrl]: !prev[videoUrl]
    }));
  };

  const handleMetricChange = (videoUrl: string, metric: string, value: number) => {
    setMetrics(prev => ({
      ...prev,
      [videoUrl]: {
        ...prev[videoUrl],
        [metric]: value
      }
    }));
  };

  const calculateTotal = () => {
    let total = subscriberCount * 1000; // 구독자 수 * 단가

    if (selectedPackage) {
      const pkg = packages.find(p => p.name === selectedPackage);
      if (pkg) {
        total += pkg.price;
      }
    }

    // 선택된 영상의 지표별 가격 계산
    Object.entries(selectedVideos).forEach(([url, isSelected]) => {
      if (isSelected && metrics[url]) {
        const videoMetrics = metrics[url];
        total += (videoMetrics.views * 100) + // 조회수 당 100원
                (videoMetrics.likes * 200) + // 좋아요 당 200원
                (videoMetrics.comments * 500); // 댓글 당 500원
      }
    });

    return total;
  };

  // 선택된 영상의 지표별 가격 계산 함수
  const calculateMetricsPrice = (url: string) => {
    if (!metrics[url]) return { views: 0, likes: 0, comments: 0 };
    return {
      views: metrics[url].views * 100,
      likes: metrics[url].likes * 200,
      comments: metrics[url].comments * 500
    };
  };

  // 성과 지표에 따른 색상 반환 함수
  const getPerformanceColor = (performance?: string) => {
    switch (performance) {
      case '우수':
        return 'primary';
      case '적정':
        return 'success';
      case '미달':
        return 'error';
      default:
        return 'default';
    }
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>
        1. 구독자 늘리기
      </Typography>
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <TextField
              type="number"
              label="늘릴 구독자 수"
              variant="outlined"
              value={subscriberCount || ''}
              onChange={(e) => setSubscriberCount(Number(e.target.value))}
              InputProps={{
                inputProps: { min: 0, step: 100 }
              }}
              sx={{ width: 200 }}
            />
            <Typography variant="body1" sx={{ ml: 2 }}>
              가격: ₩{(subscriberCount * 1000).toLocaleString()}
            </Typography>
          </Box>
        </CardContent>
      </Card>

      <Typography variant="h5" gutterBottom>
        2. 게시물 지표 늘리기
      </Typography>

      <Box sx={{ mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          패키지 선택
        </Typography>
        <RadioGroup
          value={selectedPackage}
          onChange={(e) => setSelectedPackage(e.target.value)}
        >
          <Grid container spacing={2}>
            {packages.map((pkg) => (
              <Grid item xs={3} key={pkg.name}>
                <Card>
                  <CardContent>
                    <FormControlLabel
                      value={pkg.name}
                      control={<Radio />}
                      label={
                        <Box>
                          <Typography variant="h6">{pkg.name}</Typography>
                          <Typography>₩{pkg.price.toLocaleString()}</Typography>
                        </Box>
                      }
                    />
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </RadioGroup>
      </Box>

      <TableContainer component={Paper} sx={{ mb: 2 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell padding="checkbox" style={{ width: '50px' }}>선택</TableCell>
              <TableCell style={{ width: '120px' }}>썸네일</TableCell>
              <TableCell style={{ width: '300px' }}>영상 정보</TableCell>
              <TableCell>성과 지표</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {videos.map((video) => (
              <React.Fragment key={video.url}>
                <TableRow>
                  <TableCell padding="checkbox" rowSpan={2}>
                    <Checkbox
                      checked={selectedVideos[video.url] || false}
                      onChange={() => handleVideoSelect(video.url)}
                    />
                  </TableCell>
                  <TableCell rowSpan={2}>
                    <img 
                      src={video.thumbnail} 
                      alt={video.title}
                      style={{ width: '100px', height: '56px', objectFit: 'cover' }}
                    />
                  </TableCell>
                  <TableCell>
                    <Typography variant="subtitle1" gutterBottom>
                      {video.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      업로드: {video.publishedAt}
                    </Typography>
                  </TableCell>
                  <TableCell>
                    <Box sx={{ display: 'flex', gap: 2 }}>
                      <Chip 
                        label={`조회수: ${video.performance?.views || '미달'}`}
                        color={getPerformanceColor(video.performance?.views)}
                        variant="outlined"
                      />
                      <Chip 
                        label={`좋아요: ${video.performance?.likes || '미달'}`}
                        color={getPerformanceColor(video.performance?.likes)}
                        variant="outlined"
                      />
                      <Chip 
                        label={`댓글: ${video.performance?.comments || '미달'}`}
                        color={getPerformanceColor(video.performance?.comments)}
                        variant="outlined"
                      />
                    </Box>
                  </TableCell>
                </TableRow>
                <TableRow>
                  <TableCell colSpan={2}>
                    <Box sx={{ px: 2, py: 1 }}>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" gutterBottom>조회수</Typography>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                          <Box sx={{ flex: 1 }}>
                            <Slider
                              disabled={!selectedVideos[video.url]}
                              min={0}
                              max={10000}
                              step={100}
                              valueLabelDisplay="auto"
                              onChange={(_, value) => 
                                handleMetricChange(video.url, 'views', value as number)
                              }
                            />
                          </Box>
                          {selectedVideos[video.url] && metrics[video.url]?.views > 0 && (
                            <Typography variant="body2" sx={{ minWidth: 100 }}>
                              ₩{(metrics[video.url].views * 100).toLocaleString()}
                            </Typography>
                          )}
                        </Box>
                      </Box>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" gutterBottom>좋아요</Typography>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                          <Box sx={{ flex: 1 }}>
                            <Slider
                              disabled={!selectedVideos[video.url]}
                              min={0}
                              max={1000}
                              step={10}
                              valueLabelDisplay="auto"
                              onChange={(_, value) => 
                                handleMetricChange(video.url, 'likes', value as number)
                              }
                            />
                          </Box>
                          {selectedVideos[video.url] && metrics[video.url]?.likes > 0 && (
                            <Typography variant="body2" sx={{ minWidth: 100 }}>
                              ₩{(metrics[video.url].likes * 200).toLocaleString()}
                            </Typography>
                          )}
                        </Box>
                      </Box>
                      <Box>
                        <Typography variant="body2" gutterBottom>댓글</Typography>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                          <Box sx={{ flex: 1 }}>
                            <Slider
                              disabled={!selectedVideos[video.url]}
                              min={0}
                              max={100}
                              step={1}
                              valueLabelDisplay="auto"
                              onChange={(_, value) => 
                                handleMetricChange(video.url, 'comments', value as number)
                              }
                            />
                          </Box>
                          {selectedVideos[video.url] && metrics[video.url]?.comments > 0 && (
                            <Typography variant="body2" sx={{ minWidth: 100 }}>
                              ₩{(metrics[video.url].comments * 500).toLocaleString()}
                            </Typography>
                          )}
                        </Box>
                      </Box>
                      {selectedVideos[video.url] && (
                        <Box sx={{ mt: 1, textAlign: 'right' }}>
                          <Typography variant="subtitle2" color="primary">
                            영상 총액: ₩{(
                              (metrics[video.url]?.views || 0) * 100 +
                              (metrics[video.url]?.likes || 0) * 200 +
                              (metrics[video.url]?.comments || 0) * 500
                            ).toLocaleString()}
                          </Typography>
                        </Box>
                      )}
                    </Box>
                  </TableCell>
                </TableRow>
              </React.Fragment>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Box sx={{ mt: 4, p: 2, bgcolor: 'background.paper' }}>
        <Typography variant="h6" gutterBottom>
          견적서
        </Typography>
        <Typography>
          구독자 늘리기: ₩{(subscriberCount * 1000).toLocaleString()}
        </Typography>
        {selectedPackage && (
          <Typography>
            {selectedPackage}: ₩{packages.find(p => p.name === selectedPackage)?.price.toLocaleString()}
          </Typography>
        )}
        {Object.entries(selectedVideos).map(([url, isSelected]) => {
          if (isSelected && metrics[url]) {
            const prices = calculateMetricsPrice(url);
            const video = videos.find(v => v.url === url);
            return (
              <Typography key={url}>
                {video?.title}: ₩{(prices.views + prices.likes + prices.comments).toLocaleString()}
              </Typography>
            );
          }
          return null;
        })}
        <Typography variant="h6" sx={{ mt: 2 }}>
          총 견적: ₩{calculateTotal().toLocaleString()}
        </Typography>
      </Box>
    </Box>
  );
};
