import React, { useState } from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  Slider,
  FormControlLabel,
  Checkbox,
  Radio,
  RadioGroup,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  TextField
} from '@mui/material';

interface Video {
  title: string;
  publishedAt: string;
  url: string;
  thumbnail: string;
  statistics: {
    viewCount: string;
    likeCount: string;
    commentCount: string;
  };
}

interface ProductSelectionProps {
  videos: Video[];
  onProductSelect: (selection: any) => void;
}

const packages = [
  { name: 'BASIC 패키지', price: 100000 },
  { name: 'STANDARD 패키지', price: 200000 },
  { name: 'PREMIUM 패키지', price: 300000 },
  { name: 'VIP 패키지', price: 500000 }
];

export const ProductSelection: React.FC<ProductSelectionProps> = ({ videos = [], onProductSelect }) => {
  const [selectedVideos, setSelectedVideos] = useState<{[key: string]: boolean}>({});
  const [subscriberCount, setSubscriberCount] = useState<number>(0);
  const [selectedPackage, setSelectedPackage] = useState<string>('');
  const [metrics, setMetrics] = useState<{[key: string]: {
    views: number;
    likes: number;
    comments: number;
  }}>({});

  const handleVideoSelect = (videoUrl: string) => {
    setSelectedVideos(prev => ({
      ...prev,
      [videoUrl]: !prev[videoUrl]
    }));
  };

  const handleMetricChange = (videoUrl: string, metric: string, value: number) => {
    setMetrics(prev => ({
      ...prev,
      [videoUrl]: {
        ...prev[videoUrl],
        [metric]: value
      }
    }));
  };

  const calculateTotal = () => {
    let total = subscriberCount;

    if (selectedPackage) {
      const pkg = packages.find(p => p.name === selectedPackage);
      if (pkg) {
        total += pkg.price;
      }
    }

    return total;
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>
        1. 구독자 늘리기
      </Typography>
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <TextField
              type="number"
              label="늘릴 구독자 수"
              variant="outlined"
              value={subscriberCount || ''}
              onChange={(e) => setSubscriberCount(Number(e.target.value))}
              InputProps={{
                inputProps: { min: 0, step: 100 }
              }}
              sx={{ width: 200 }}
            />
            <Typography variant="body1" sx={{ ml: 2 }}>
              가격: ₩{(subscriberCount * 1000).toLocaleString()}
            </Typography>
          </Box>
        </CardContent>
      </Card>

      <Typography variant="h5" gutterBottom>
        2. 게시물 지표 늘리기
      </Typography>

      <Box sx={{ mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          패키지 선택
        </Typography>
        <RadioGroup
          value={selectedPackage}
          onChange={(e) => setSelectedPackage(e.target.value)}
        >
          <Grid container spacing={2}>
            {packages.map((pkg) => (
              <Grid item xs={3} key={pkg.name}>
                <Card>
                  <CardContent>
                    <FormControlLabel
                      value={pkg.name}
                      control={<Radio />}
                      label={
                        <Box>
                          <Typography variant="h6">{pkg.name}</Typography>
                          <Typography>₩{pkg.price.toLocaleString()}</Typography>
                        </Box>
                      }
                    />
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </RadioGroup>
      </Box>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell padding="checkbox" style={{ width: '50px' }}>선택</TableCell>
              <TableCell style={{ width: '120px' }}>썸네일</TableCell>
              <TableCell style={{ width: '250px' }}>영상 제목</TableCell>
              <TableCell style={{ width: '120px' }}>업로드 일자</TableCell>
              <TableCell>조회수</TableCell>
              <TableCell>좋아요</TableCell>
              <TableCell>댓글</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {videos.map((video) => (
              <TableRow key={video.url}>
                <TableCell padding="checkbox">
                  <Checkbox
                    checked={selectedVideos[video.url] || false}
                    onChange={() => handleVideoSelect(video.url)}
                  />
                </TableCell>
                <TableCell>
                  <img 
                    src={video.thumbnail} 
                    alt={video.title}
                    style={{ width: '100px', height: '56px', objectFit: 'cover' }}
                  />
                </TableCell>
                <TableCell>{video.title}</TableCell>
                <TableCell>{video.publishedAt}</TableCell>
                <TableCell sx={{ minWidth: 200 }}>
                  <Slider
                    disabled={!selectedVideos[video.url]}
                    min={0}
                    max={10000}
                    step={100}
                    valueLabelDisplay="auto"
                    onChange={(_, value) => 
                      handleMetricChange(video.url, 'views', value as number)
                    }
                  />
                </TableCell>
                <TableCell sx={{ minWidth: 200 }}>
                  <Slider
                    disabled={!selectedVideos[video.url]}
                    min={0}
                    max={1000}
                    step={10}
                    valueLabelDisplay="auto"
                    onChange={(_, value) => 
                      handleMetricChange(video.url, 'likes', value as number)
                    }
                  />
                </TableCell>
                <TableCell sx={{ minWidth: 200 }}>
                  <Slider
                    disabled={!selectedVideos[video.url]}
                    min={0}
                    max={100}
                    step={1}
                    valueLabelDisplay="auto"
                    onChange={(_, value) => 
                      handleMetricChange(video.url, 'comments', value as number)
                    }
                  />
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Box sx={{ mt: 4, p: 2, bgcolor: 'background.paper' }}>
        <Typography variant="h6" gutterBottom>
          견적서
        </Typography>
        <Typography>
          구독자 늘리기: ₩{(subscriberCount * 1000).toLocaleString()}
        </Typography>
        {selectedPackage && (
          <Typography>
            {selectedPackage}: ₩{packages.find(p => p.name === selectedPackage)?.price.toLocaleString()}
          </Typography>
        )}
        <Typography variant="h6" sx={{ mt: 2 }}>
          총 견적: ₩{calculateTotal().toLocaleString()}
        </Typography>
      </Box>
    </Box>
  );
};
