import React, { useState } from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  Slider,
  FormControlLabel,
  Checkbox,
  Radio,
  RadioGroup,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  TextField,
  Chip
} from '@mui/material';

interface Video {
  title: string;
  publishedAt: string;
  url: string;
  videoId: string;
  thumbnail: string;
  statistics: {
    viewCount: string;
    likeCount: string;
    commentCount: string;
  };
  performance?: {
    views: string;
    likes: string;
    comments: string;
  };
}

interface MetricPackage {
  name: string;
  price: number;
  formula: {
    views: (v: number, baseValue: number) => number;
    likes: (v: number, baseValue: number) => number;
    comments: (v: number, baseValue: number) => number;
  };
}

interface ProductSelectionProps {
  videos: Video[];
  onProductSelect: (selection: any) => void;
}

const packages: MetricPackage[] = [
  {
    name: 'BASIC 패키지',
    price: 100000,
    formula: {
      views: (v, baseValue) => v <= baseValue ? 0 : v - baseValue,
      likes: (v, baseValue) => v <= baseValue ? 0 : v - baseValue,
      comments: (v, baseValue) => v <= baseValue ? 0 : v - baseValue
    }
  },
  {
    name: 'STANDARD 패키지',
    price: 200000,
    formula: {
      views: (v, baseValue) => v <= baseValue ? 0 : ((v - baseValue) * 1.5),
      likes: (v, baseValue) => v <= baseValue ? 0 : ((v - baseValue) * 1.5),
      comments: (v, baseValue) => v <= baseValue ? 0 : ((v - baseValue) * 1.5)
    }
  },
  {
    name: 'PREMIUM 패키지',
    price: 300000,
    formula: {
      views: (v, baseValue) => v <= baseValue ? 0 : ((v - baseValue) * 2),
      likes: (v, baseValue) => v <= baseValue ? 0 : ((v - baseValue) * 2),
      comments: (v, baseValue) => v <= baseValue ? 0 : ((v - baseValue) * 2)
    }
  },
  {
    name: 'VIP 패키지',
    price: 500000,
    formula: {
      views: (v, baseValue) => v <= baseValue ? 0 : ((v - baseValue) * 3),
      likes: (v, baseValue) => v <= baseValue ? 0 : ((v - baseValue) * 3),
      comments: (v, baseValue) => v <= baseValue ? 0 : ((v - baseValue) * 3)
    }
  }
];

export const ProductSelection: React.FC<ProductSelectionProps> = ({ videos = [], onProductSelect }) => {
  const [selectedVideos, setSelectedVideos] = useState<{[key: string]: boolean}>({});
  const [subscriberCount, setSubscriberCount] = useState<number>(0);
  const [videoPackages, setVideoPackages] = useState<{[key: string]: string}>({});
  const [metrics, setMetrics] = useState<{[key: string]: {
    views: number;
    likes: number;
    comments: number;
    baseViews: number;
    baseLikes: number;
    baseComments: number;
  }}>({});

  const handleVideoSelect = (videoUrl: string) => {
    setSelectedVideos(prev => ({
      ...prev,
      [videoUrl]: !prev[videoUrl]
    }));
    // 비디오 선택 시 기본값 설정
    if (!metrics[videoUrl]) {
      const video = videos.find(v => v.url === videoUrl);
      if (video) {
        setMetrics(prev => ({
          ...prev,
          [videoUrl]: {
            views: 0,
            likes: 0,
            comments: 0,
            baseViews: parseInt(video.statistics.viewCount),
            baseLikes: parseInt(video.statistics.likeCount),
            baseComments: parseInt(video.statistics.commentCount)
          }
        }));
      }
    }
  };

  const handlePackageSelect = (videoUrl: string, packageName: string) => {
    setVideoPackages(prev => ({
      ...prev,
      [videoUrl]: packageName
    }));

    // 패키지 선택 시 자동으로 슬라이더 값 조정
    const video = videos.find(v => v.url === videoUrl);
    const metricData = metrics[videoUrl];
    if (video && metricData) {
      const pkg = packages.find(p => p.name === packageName);
      if (pkg) {
        // 각 지표별 기본값에서 패키지별 증가량 계산
        const baseViews = metricData.baseViews;
        const baseLikes = metricData.baseLikes;
        const baseComments = metricData.baseComments;

        // 패키지별 증가량 계산 (BASIC: 10%, STANDARD: 20%, PREMIUM: 30%, VIP: 50%)
        const multiplier = 
          packageName === 'BASIC 패키지' ? 0.1 :
          packageName === 'STANDARD 패키지' ? 0.2 :
          packageName === 'PREMIUM 패키지' ? 0.3 :
          packageName === 'VIP 패키지' ? 0.5 : 0;

        setMetrics(prev => ({
          ...prev,
          [videoUrl]: {
            ...prev[videoUrl],
            views: Math.round(baseViews * (1 + multiplier)),
            likes: Math.round(baseLikes * (1 + multiplier)),
            comments: Math.round(baseComments * (1 + multiplier))
          }
        }));
      }
    }
  };

  const handleMetricChange = (videoUrl: string, metric: string, value: number) => {
    setMetrics(prev => ({
      ...prev,
      [videoUrl]: {
        ...prev[videoUrl],
        [metric]: value
      }
    }));
  };

  const calculateMetricIncrease = (videoUrl: string, metric: string) => {
    const metricData = metrics[videoUrl];
    if (!metricData) return 0;

    const baseValue = metricData[`base${metric.charAt(0).toUpperCase() + metric.slice(1)}` as keyof typeof metricData];
    const currentValue = metricData[metric as keyof typeof metricData];

    return Math.max(0, currentValue - baseValue);
  };

  const calculateMetricPrice = (videoUrl: string, metric: string) => {
    const selectedPackage = packages.find(p => p.name === videoPackages[videoUrl]);
    const increase = calculateMetricIncrease(videoUrl, metric);

    if (!selectedPackage || increase <= 0) return 0;

    const pricePerUnit = {
      views: 100,
      likes: 200,
      comments: 500
    };

    return increase * pricePerUnit[metric as keyof typeof pricePerUnit];
  };

  const calculateTotal = () => {
    let total = subscriberCount * 1000; // 구독자 수 * 단가

    if (videoPackages['']) {
      const pkg = packages.find(p => p.name === videoPackages['']);
      if (pkg) {
        total += pkg.price;
      }
    }

    // 선택된 영상의 지표별 가격 계산
    Object.entries(selectedVideos).forEach(([url, isSelected]) => {
      if (isSelected && metrics[url]) {
        const videoMetrics = metrics[url];
        total += (videoMetrics.views * 100) + // 조회수 당 100원
                (videoMetrics.likes * 200) + // 좋아요 당 200원
                (videoMetrics.comments * 500); // 댓글 당 500원
      }
    });

    return total;
  };

  // 선택된 영상의 지표별 가격 계산 함수
  const calculateMetricsPrice = (url: string) => {
    if (!metrics[url]) return { views: 0, likes: 0, comments: 0 };
    return {
      views: metrics[url].views * 100,
      likes: metrics[url].likes * 200,
      comments: metrics[url].comments * 500
    };
  };

  // 성과 지표에 따른 색상 반환 함수
  const getPerformanceColor = (performance?: string) => {
    switch (performance) {
      case '우수':
        return 'primary';
      case '적정':
        return 'success';
      case '미달':
        return 'error';
      default:
        return 'default';
    }
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>
        1. 구독자 늘리기
      </Typography>
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <TextField
              type="number"
              label="늘릴 구독자 수"
              variant="outlined"
              value={subscriberCount || ''}
              onChange={(e) => setSubscriberCount(Number(e.target.value))}
              InputProps={{
                inputProps: { min: 0, step: 100 }
              }}
              sx={{ width: 200 }}
            />
            <Typography variant="body1" sx={{ ml: 2 }}>
              가격: ₩{(subscriberCount * 1000).toLocaleString()}
            </Typography>
          </Box>
        </CardContent>
      </Card>

      <Typography variant="h5" gutterBottom>
        2. 게시물 지표 늘리기
      </Typography>

      <Box sx={{ mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          패키지 선택
        </Typography>
        <RadioGroup
          value={videoPackages[''] || ''}
          onChange={(e) => handlePackageSelect('', e.target.value)}
        >
          <Grid container spacing={2}>
            {packages.map((pkg) => (
              <Grid item xs={3} key={pkg.name}>
                <Card>
                  <CardContent>
                    <FormControlLabel
                      value={pkg.name}
                      control={<Radio />}
                      label={
                        <Box>
                          <Typography variant="h6">{pkg.name}</Typography>
                          <Typography>₩{pkg.price.toLocaleString()}</Typography>
                        </Box>
                      }
                    />
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </RadioGroup>
      </Box>

      <TableContainer component={Paper} sx={{ mb: 2 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell padding="checkbox" style={{ width: '50px' }}>선택</TableCell>
              <TableCell style={{ width: '120px' }}>썸네일</TableCell>
              <TableCell style={{ width: '300px' }}>영상 정보</TableCell>
              <TableCell>성과 지표</TableCell>
              <TableCell style={{ width: '200px' }}>패키지 선택</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {videos.map((video) => (
              <React.Fragment key={video.url}>
                <TableRow>
                  <TableCell padding="checkbox" rowSpan={2}>
                    <Checkbox
                      checked={selectedVideos[video.url] || false}
                      onChange={() => handleVideoSelect(video.url)}
                    />
                  </TableCell>
                  <TableCell rowSpan={2}>
                    <img 
                      src={video.thumbnail} 
                      alt={video.title}
                      style={{ width: '100px', height: '56px', objectFit: 'cover' }}
                    />
                  </TableCell>
                  <TableCell>
                    <Typography variant="subtitle1" gutterBottom>
                      {video.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      업로드: {video.publishedAt}
                    </Typography>
                  </TableCell>
                  <TableCell>
                    <Box sx={{ display: 'flex', gap: 2 }}>
                      <Chip 
                        label={`조회수: ${video.performance?.views || '미달'}`}
                        color={getPerformanceColor(video.performance?.views)}
                        variant="outlined"
                      />
                      <Chip 
                        label={`좋아요: ${video.performance?.likes || '미달'}`}
                        color={getPerformanceColor(video.performance?.likes)}
                        variant="outlined"
                      />
                      <Chip 
                        label={`댓글: ${video.performance?.comments || '미달'}`}
                        color={getPerformanceColor(video.performance?.comments)}
                        variant="outlined"
                      />
                    </Box>
                  </TableCell>
                  <TableCell>
                    <RadioGroup
                      value={videoPackages[video.url] || ''}
                      onChange={(e) => handlePackageSelect(video.url, e.target.value)}
                    >
                      {packages.map((pkg) => (
                        <FormControlLabel
                          key={pkg.name}
                          value={pkg.name}
                          control={<Radio size="small" />}
                          label={pkg.name}
                          disabled={!selectedVideos[video.url]}
                        />
                      ))}
                    </RadioGroup>
                  </TableCell>
                </TableRow>
                <TableRow>
                  <TableCell colSpan={3}>
                    <Box sx={{ px: 2, py: 1 }}>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" gutterBottom>조회수</Typography>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                          <Box sx={{ flex: 1 }}>
                            <Slider
                              disabled={!selectedVideos[video.url] || !videoPackages[video.url]}
                              min={metrics[video.url]?.baseViews || 0}
                              max={(metrics[video.url]?.baseViews || 0) * 2} // 최대 200%까지 증가 가능
                              step={100}
                              valueLabelDisplay="auto"
                              value={metrics[video.url]?.views || 0}
                              onChange={(_, value) => 
                                handleMetricChange(video.url, 'views', value as number)
                              }
                            />
                          </Box>
                          {selectedVideos[video.url] && videoPackages[video.url] && (
                            <Box sx={{ minWidth: 200 }}>
                              <Typography variant="body2">
                                증가량: {calculateMetricIncrease(video.url, 'views').toLocaleString()}회
                              </Typography>
                              <Typography variant="body2">
                                가격: ₩{calculateMetricPrice(video.url, 'views').toLocaleString()}
                              </Typography>
                            </Box>
                          )}
                        </Box>
                      </Box>
                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" gutterBottom>좋아요</Typography>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                          <Box sx={{ flex: 1 }}>
                            <Slider
                              disabled={!selectedVideos[video.url] || !videoPackages[video.url]}
                              min={metrics[video.url]?.baseLikes || 0}
                              max={(metrics[video.url]?.baseLikes || 0) * 2}
                              step={10}
                              valueLabelDisplay="auto"
                              value={metrics[video.url]?.likes || 0}
                              onChange={(_, value) => 
                                handleMetricChange(video.url, 'likes', value as number)
                              }
                            />
                          </Box>
                          {selectedVideos[video.url] && videoPackages[video.url] && (
                            <Box sx={{ minWidth: 200 }}>
                              <Typography variant="body2">
                                증가량: {calculateMetricIncrease(video.url, 'likes').toLocaleString()}개
                              </Typography>
                              <Typography variant="body2">
                                가격: ₩{calculateMetricPrice(video.url, 'likes').toLocaleString()}
                              </Typography>
                            </Box>
                          )}
                        </Box>
                      </Box>
                      <Box>
                        <Typography variant="body2" gutterBottom>댓글</Typography>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                          <Box sx={{ flex: 1 }}>
                            <Slider
                              disabled={!selectedVideos[video.url] || !videoPackages[video.url]}
                              min={metrics[video.url]?.baseComments || 0}
                              max={(metrics[video.url]?.baseComments || 0) * 2}
                              step={1}
                              valueLabelDisplay="auto"
                              value={metrics[video.url]?.comments || 0}
                              onChange={(_, value) => 
                                handleMetricChange(video.url, 'comments', value as number)
                              }
                            />
                          </Box>
                          {selectedVideos[video.url] && videoPackages[video.url] && (
                            <Box sx={{ minWidth: 200 }}>
                              <Typography variant="body2">
                                증가량: {calculateMetricIncrease(video.url, 'comments').toLocaleString()}개
                              </Typography>
                              <Typography variant="body2">
                                가격: ₩{calculateMetricPrice(video.url, 'comments').toLocaleString()}
                              </Typography>
                            </Box>
                          )}
                        </Box>
                      </Box>
                      {selectedVideos[video.url] && videoPackages[video.url] && (
                        <Box sx={{ mt: 1, textAlign: 'right' }}>
                          <Typography variant="subtitle2" color="primary">
                            영상 총액: ₩{(
                              calculateMetricPrice(video.url, 'views') +
                              calculateMetricPrice(video.url, 'likes') +
                              calculateMetricPrice(video.url, 'comments')
                            ).toLocaleString()}
                          </Typography>
                        </Box>
                      )}
                    </Box>
                  </TableCell>
                </TableRow>
              </React.Fragment>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Box sx={{ mt: 4, p: 2, bgcolor: 'background.paper' }}>
        <Typography variant="h6" gutterBottom>
          견적서
        </Typography>
        <Typography>
          구독자 늘리기: {subscriberCount.toLocaleString()}명 (₩{(subscriberCount * 1000).toLocaleString()})
        </Typography>
        {Object.entries(selectedVideos).map(([url, isSelected]) => {
          if (isSelected && metrics[url] && videoPackages[url]) {
            const video = videos.find(v => v.url === url);
            const viewsIncrease = calculateMetricIncrease(url, 'views');
            const likesIncrease = calculateMetricIncrease(url, 'likes');
            const commentsIncrease = calculateMetricIncrease(url, 'comments');
            const totalPrice = 
              calculateMetricPrice(url, 'views') +
              calculateMetricPrice(url, 'likes') +
              calculateMetricPrice(url, 'comments');

            return (
              <Box key={url} sx={{ mt: 1 }}>
                <Typography variant="subtitle1">{video?.title}</Typography>
                <Typography variant="body2" sx={{ ml: 2 }}>
                  패키지: {videoPackages[url]}
                </Typography>
                <Typography variant="body2" sx={{ ml: 2 }}>
                  조회수: +{viewsIncrease.toLocaleString()}회
                </Typography>
                <Typography variant="body2" sx={{ ml: 2 }}>
                  좋아요: +{likesIncrease.toLocaleString()}개
                </Typography>
                <Typography variant="body2" sx={{ ml: 2 }}>
                  댓글: +{commentsIncrease.toLocaleString()}개
                </Typography>
                <Typography variant="body2" sx={{ ml: 2 }}>
                  가격: ₩{totalPrice.toLocaleString()}
                </Typography>
              </Box>
            );
          }
          return null;
        })}
        <Typography variant="h6" sx={{ mt: 2 }}>
          총 견적: ₩{calculateTotal().toLocaleString()}
        </Typography>
      </Box>
    </Box>
  );
};
