import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Checkbox,
  Slider,
  FormControlLabel,
  Paper,
  Grid,
  Card,
  CardContent,
  Radio,
  RadioGroup,
  Divider,
  Chip,
  CardMedia
} from '@mui/material';

interface Video {
  title: string;
  publishedAt: string;
  thumbnail: string;
  performance?: {
    views: string;
    likes: string;
    comments: string;
  };
  statistics?: {
    viewCount: string;
    likeCount: string;
    commentCount: string;
    subscriberCount?: string;
  };
}

interface ProductSelectionProps {
  videos: Video[];
  onProductSelect: (selection: any) => void;
}

interface PackageType {
  name: string;
  price: number;
  count: number;
  formula: {
    views: (v: number) => number;
    likes: (l: number) => number;
    comments: (c: number) => number;
  };
}

const packages: PackageType[] = [
  { 
    name: 'BASIC 패키지', 
    price: 100000, 
    count: 0,
    formula: {
      views: (v: number) => 20,
      likes: (l: number) => 15,
      comments: (c: number) => 10
    }
  },
  { 
    name: 'STANDARD 패키지', 
    price: 200000, 
    count: 0,
    formula: {
      views: (v: number) => 40,
      likes: (l: number) => 30,
      comments: (c: number) => 20
    }
  },
  { 
    name: 'PREMIUM 패키지', 
    price: 300000, 
    count: 0,
    formula: {
      views: (v: number) => 60,
      likes: (l: number) => 45,
      comments: (c: number) => 30
    }
  },
  { 
    name: 'VIP 패키지', 
    price: 500000, 
    count: 0,
    formula: {
      views: (v: number) => 100,
      likes: (l: number) => 80,
      comments: (c: number) => 50
    }
  },
];

const ProductSelection: React.FC<ProductSelectionProps> = ({ videos, onProductSelect }) => {
  const [selectedVideos, setSelectedVideos] = useState<{ [key: string]: boolean }>({});
  const [videoPackages, setVideoPackages] = useState<{ [key: string]: string }>({});
  const [videoMetrics, setVideoMetrics] = useState<{
    [key: string]: {
      views: number;
      likes: number;
      comments: number;
    };
  }>({});
  const [packageCounts, setPackageCounts] = useState<PackageType[]>(packages);
  const [totalAmount, setTotalAmount] = useState<number>(0);
  const [videoTotals, setVideoTotals] = useState<{ [key: string]: number }>({});
  const [subscriberCount, setSubscriberCount] = useState<number>(1000);
  const [isSubscriberSelected, setIsSubscriberSelected] = useState<boolean>(false);

  const getPackageIncreaseRange = (packageName: string): { min: number; max: number } => {
    switch (packageName) {
      case 'BASIC 패키지':
        return { min: 20, max: 50 };
      case 'STANDARD 패키지':
        return { min: 50, max: 100 };
      case 'PREMIUM 패키지':
        return { min: 100, max: 200 };
      case 'VIP 패키지':
        return { min: 200, max: 500 };
      default:
        return { min: 0, max: 50 };
    }
  };

  const handleVideoSelect = (videoTitle: string) => {
    setSelectedVideos(prev => ({
      ...prev,
      [videoTitle]: !prev[videoTitle]
    }));
  };

  const handlePackageSelect = (videoTitle: string, packageName: string) => {
    setVideoPackages(prev => ({
      ...prev,
      [videoTitle]: packageName
    }));

    const range = getPackageIncreaseRange(packageName);
    setVideoMetrics(prev => ({
      ...prev,
      [videoTitle]: {
        views: range.min,
        likes: range.min,
        comments: range.min
      }
    }));
  };

  const handleMetricChange = (videoTitle: string, metric: 'views' | 'likes' | 'comments', value: number) => {
    const packageName = videoPackages[videoTitle];

    if (packageName) {
      const range = getPackageIncreaseRange(packageName);
      const adjustedValue = Math.min(Math.max(value, range.min), range.max);

      setVideoMetrics(prev => ({
        ...prev,
        [videoTitle]: {
          ...prev[videoTitle],
          [metric]: adjustedValue
        }
      }));
    }
  };

  useEffect(() => {
    // 패키지 카운트 및 금액 계산
    const counts = packages.map(pkg => ({
      ...pkg,
      count: Object.values(videoPackages).filter(p => p === pkg.name).length
    }));
    setPackageCounts(counts);

    // 총액 계산
    let total = 0;
    const newVideoTotals: { [key: string]: number } = {};

    Object.entries(videoPackages).forEach(([videoTitle, packageName]) => {
      const pkg = counts.find(p => p.name === packageName);
      const metrics = videoMetrics[videoTitle];

      if (pkg && metrics) {
        const videoTotal = pkg.price + ((metrics.views + metrics.likes + metrics.comments) * 1000);
        newVideoTotals[videoTitle] = videoTotal;
        total += videoTotal;
      }
    });

    // 구독자 상품 금액 추가
    if (isSubscriberSelected) {
      total += subscriberCount * 500;
    }

    setVideoTotals(newVideoTotals);
    setTotalAmount(total);
  }, [videoPackages, videoMetrics, subscriberCount, isSubscriberSelected]);

  return (
    <Box>
      {/* 구독자 단품 상품 */}
      <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            1. 구독자 수 증가 단품
          </Typography>
          <FormControlLabel
            control={
              <Checkbox
                checked={isSubscriberSelected}
                onChange={(e) => setIsSubscriberSelected(e.target.checked)}
              />
            }
            label="선택"
          />
        </Box>
        <Box sx={{ mt: 2 }}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <Typography gutterBottom>목표 구독자 수 설정</Typography>
              <Slider
                value={subscriberCount}
                onChange={(_, value) => setSubscriberCount(value as number)}
                min={1000}
                max={100000}
                step={1000}
                valueLabelDisplay="auto"
                valueLabelFormat={(value) => `${value.toLocaleString()}명`}
                sx={{ width: '100%' }}
                disabled={!isSubscriberSelected}
              />
            </Grid>
            <Grid item xs={12}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
                <Typography variant="body1">
                  설정된 구독자 수: {subscriberCount.toLocaleString()}명
                </Typography>
                <Typography variant="h6" color="primary">
                  금액: ₩{(isSubscriberSelected ? subscriberCount * 500 : 0).toLocaleString()}
                </Typography>
              </Box>
            </Grid>
          </Grid>
        </Box>
      </Paper>

      {/* 영상별 패키지 상품 */}
      <Paper elevation={3} sx={{ p: 3 }}>
        <Typography variant="h6" gutterBottom>
          2. 영상별 패키지 선택
        </Typography>
        {videos.map((video, index) => (
          <Box key={index} sx={{ mb: 4 }}>
            <Grid container spacing={2}>
              {/* 첫 번째 줄: 썸네일, 제목, 날짜, 체크박스 */}
              <Grid item xs={12}>
                <Card variant="outlined">
                  <CardContent>
                    <Grid container spacing={2} alignItems="center">
                      <Grid item xs={2}>
                        <CardMedia
                          component="img"
                          image={video.thumbnail}
                          alt={video.title}
                          sx={{ borderRadius: 1 }}
                        />
                      </Grid>
                      <Grid item xs={9}>
                        <Typography variant="h6">{video.title}</Typography>
                        <Typography variant="body2" color="text.secondary">
                          {new Date(video.publishedAt).toLocaleDateString('ko-KR', {
                            year: 'numeric',
                            month: 'long',
                            day: 'numeric'
                          })}
                        </Typography>
                        {video.performance && (
                          <Box sx={{ mt: 1, display: 'flex', gap: 1 }}>
                            <Chip 
                              label={`조회수: ${video.performance.views}`}
                              color={video.performance.views === '우수' ? 'primary' : 
                                    video.performance.views === '적정' ? 'success' : 'error'}
                              size="small"
                            />
                            <Chip 
                              label={`좋아요: ${video.performance.likes}`}
                              color={video.performance.likes === '우수' ? 'primary' : 
                                    video.performance.likes === '적정' ? 'success' : 'error'}
                              size="small"
                            />
                            <Chip 
                              label={`댓글: ${video.performance.comments}`}
                              color={video.performance.comments === '우수' ? 'primary' : 
                                    video.performance.comments === '적정' ? 'success' : 'error'}
                              size="small"
                            />
                          </Box>
                        )}
                      </Grid>
                      <Grid item xs={1}>
                        <Checkbox
                          checked={selectedVideos[video.title] || false}
                          onChange={() => handleVideoSelect(video.title)}
                        />
                      </Grid>
                    </Grid>
                  </CardContent>
                </Card>
              </Grid>

              {/* 두 번째 줄: 패키지 선택 및 슬라이더 */}
              {selectedVideos[video.title] && (
                <Grid item xs={12}>
                  <Card variant="outlined">
                    <CardContent>
                      <Typography variant="subtitle1" gutterBottom>
                        패키지 선택
                      </Typography>
                      <RadioGroup
                        value={videoPackages[video.title] || ''}
                        onChange={(e) => handlePackageSelect(video.title, e.target.value)}
                      >
                        <Grid container spacing={2}>
                          {packages.map((pkg) => (
                            <Grid item xs={3} key={pkg.name}>
                              <Card variant="outlined">
                                <CardContent>
                                  <FormControlLabel
                                    value={pkg.name}
                                    control={<Radio />}
                                    label={
                                      <Box>
                                        <Typography variant="subtitle2">{pkg.name}</Typography>
                                        <Typography variant="body2" color="text.secondary">
                                          ₩{pkg.price.toLocaleString()}
                                        </Typography>
                                      </Box>
                                    }
                                  />
                                </CardContent>
                              </Card>
                            </Grid>
                          ))}
                        </Grid>
                      </RadioGroup>

                      <Box sx={{ mt: 3 }}>
                        <Typography variant="subtitle1" gutterBottom>
                          세부 지표 설정
                        </Typography>
                        <Grid container spacing={2}>
                          <Grid item xs={4}>
                            <Typography gutterBottom>조회수 증가</Typography>
                            <Slider
                              value={videoMetrics[video.title]?.views || 0}
                              onChange={(_, value) => handleMetricChange(video.title, 'views', value as number)}
                              min={0}
                              max={100}
                              valueLabelDisplay="auto"
                              valueLabelFormat={(value) => {
                                const currentValue = parseInt(video.statistics?.viewCount || '0');
                                const increase = Math.round(currentValue * (value / 100));
                                return `+${increase.toLocaleString()}회 (${value}%)`;
                              }}
                            />
                          </Grid>
                          <Grid item xs={4}>
                            <Typography gutterBottom>좋아요 증가</Typography>
                            <Slider
                              value={videoMetrics[video.title]?.likes || 0}
                              onChange={(_, value) => handleMetricChange(video.title, 'likes', value as number)}
                              min={0}
                              max={100}
                              valueLabelDisplay="auto"
                              valueLabelFormat={(value) => {
                                const currentValue = parseInt(video.statistics?.likeCount || '0');
                                const increase = Math.round(currentValue * (value / 100));
                                return `+${increase.toLocaleString()}개 (${value}%)`;
                              }}
                            />
                          </Grid>
                          <Grid item xs={4}>
                            <Typography gutterBottom>댓글 증가</Typography>
                            <Slider
                              value={videoMetrics[video.title]?.comments || 0}
                              onChange={(_, value) => handleMetricChange(video.title, 'comments', value as number)}
                              min={0}
                              max={100}
                              valueLabelDisplay="auto"
                              valueLabelFormat={(value) => {
                                const currentValue = parseInt(video.statistics?.commentCount || '0');
                                const increase = Math.round(currentValue * (value / 100));
                                return `+${increase.toLocaleString()}개 (${value}%)`;
                              }}
                            />
                          </Grid>
                        </Grid>
                      </Box>

                      {/* 영상별 금액 표시 */}
                      <Box sx={{ mt: 3, textAlign: 'right' }}>
                        <Typography variant="h6" color="primary">
                          영상 총액: ₩{videoTotals[video.title]?.toLocaleString() || '0'}
                        </Typography>
                      </Box>
                    </CardContent>
                  </Card>
                </Grid>
              )}
            </Grid>
            {index < videos.length - 1 && <Divider sx={{ my: 3 }} />}
          </Box>
        ))}

        {/* 패키지 요약 및 총액 */}
        <Box sx={{ mt: 4 }}>
          <Typography variant="h6" gutterBottom>
            선택한 패키지 요약
          </Typography>
          <Grid container spacing={2}>
            {packageCounts.map((pkg) => (
              <Grid item xs={3} key={pkg.name}>
                <Card variant="outlined">
                  <CardContent>
                    <Typography variant="subtitle1">{pkg.name}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      {pkg.count}개 선택
                    </Typography>
                    <Typography variant="body2" color="primary">
                      ₩{(pkg.price * pkg.count).toLocaleString()}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>

          <Box sx={{ mt: 3, textAlign: 'right' }}>
            <Typography variant="h5" color="primary">
              총 금액: ₩{totalAmount.toLocaleString()}
            </Typography>
          </Box>
        </Box>
      </Paper>
    </Box>
  );
};

export default ProductSelection;
