import React, { useState } from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  Slider,
  FormControlLabel,
  Checkbox,
  Radio,
  RadioGroup,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  TextField,
  Chip
} from '@mui/material';

interface Video {
  title: string;
  publishedAt: string;
  url: string;
  videoId: string;
  thumbnail: string;
  statistics: {
    viewCount: string;
    likeCount: string;
    commentCount: string;
  };
  performance?: {
    views: string;
    likes: string;
    comments: string;
  };
}

interface MetricProduct {
  name: string;
  type: 'views' | 'likes' | 'comments';
  pricePerUnit: number;
  minAmount: number;
  maxAmount: number;
  step: number;
}

interface ProductSelectionProps {
  videos: Video[];
  onProductSelect: (selection: any) => void;
}

const products: MetricProduct[] = [
  {
    name: '조회수 늘리기',
    type: 'views',
    pricePerUnit: 100,
    minAmount: 1000,
    maxAmount: 100000,
    step: 1000
  },
  {
    name: '좋아요 늘리기',
    type: 'likes',
    pricePerUnit: 200,
    minAmount: 100,
    maxAmount: 10000,
    step: 100
  },
  {
    name: '댓글 늘리기',
    type: 'comments',
    pricePerUnit: 500,
    minAmount: 10,
    maxAmount: 1000,
    step: 10
  }
];

export const ProductSelection: React.FC<ProductSelectionProps> = ({ videos = [], onProductSelect }) => {
  const [selectedVideos, setSelectedVideos] = useState<{[key: string]: boolean}>({});
  const [subscriberCount, setSubscriberCount] = useState<number>(0);
  const [metrics, setMetrics] = useState<{[key: string]: {
    views: number;
    likes: number;
    comments: number;
    baseViews: number;
    baseLikes: number;
    baseComments: number;
  }}>({});

  const handleVideoSelect = (videoUrl: string) => {
    setSelectedVideos(prev => ({
      ...prev,
      [videoUrl]: !prev[videoUrl]
    }));
    // 비디오 선택 시 기본값 설정
    if (!metrics[videoUrl]) {
      const video = videos.find(v => v.url === videoUrl);
      if (video) {
        setMetrics(prev => ({
          ...prev,
          [videoUrl]: {
            views: parseInt(video.statistics.viewCount),
            likes: parseInt(video.statistics.likeCount),
            comments: parseInt(video.statistics.commentCount),
            baseViews: parseInt(video.statistics.viewCount),
            baseLikes: parseInt(video.statistics.likeCount),
            baseComments: parseInt(video.statistics.commentCount)
          }
        }));
      }
    }
  };

  const handleMetricChange = (videoUrl: string, metric: string, value: number) => {
    setMetrics(prev => ({
      ...prev,
      [videoUrl]: {
        ...prev[videoUrl],
        [metric]: value
      }
    }));
  };

  const calculateMetricIncrease = (videoUrl: string, metric: string) => {
    const metricData = metrics[videoUrl];
    if (!metricData) return 0;

    const baseValue = metricData[`base${metric.charAt(0).toUpperCase() + metric.slice(1)}` as keyof typeof metricData];
    const currentValue = metricData[metric as keyof typeof metricData];

    return Math.max(0, currentValue - baseValue);
  };

  const calculateMetricPrice = (videoUrl: string, metric: string) => {
    const increase = calculateMetricIncrease(videoUrl, metric);
    const product = products.find(p => p.type === metric);

    if (!product || increase <= 0) return 0;
    return increase * product.pricePerUnit;
  };

  const calculateTotal = () => {
    let total = subscriberCount * 1000; // 구독자 수 * 단가

    Object.entries(selectedVideos).forEach(([url, isSelected]) => {
      if (isSelected && metrics[url]) {
        total += calculateMetricPrice(url, 'views') +
                calculateMetricPrice(url, 'likes') +
                calculateMetricPrice(url, 'comments');
      }
    });

    return total;
  };

  // 성과 지표에 따른 색상 반환 함수
  const getPerformanceColor = (performance?: string) => {
    switch (performance) {
      case '우수':
        return 'primary';
      case '적정':
        return 'success';
      case '미달':
        return 'error';
      default:
        return 'default';
    }
  };

  const getBaseMetricValue = (videoUrl: string, type: 'views' | 'likes' | 'comments'): number => {
    const metricData = metrics[videoUrl];
    if (!metricData) return 0;

    switch (type) {
      case 'views':
        return metricData.baseViews;
      case 'likes':
        return metricData.baseLikes;
      case 'comments':
        return metricData.baseComments;
      default:
        return 0;
    }
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>
        1. 구독자 늘리기
      </Typography>
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <TextField
              type="number"
              label="늘릴 구독자 수"
              variant="outlined"
              value={subscriberCount || ''}
              onChange={(e) => setSubscriberCount(Number(e.target.value))}
              InputProps={{
                inputProps: { min: 0, step: 100 }
              }}
              sx={{ width: 200 }}
            />
            <Typography variant="body1" sx={{ ml: 2 }}>
              가격: ₩{(subscriberCount * 1000).toLocaleString()}
            </Typography>
          </Box>
        </CardContent>
      </Card>

      <Typography variant="h5" gutterBottom>
        2. 게시물 지표 늘리기
      </Typography>

      <TableContainer component={Paper} sx={{ mb: 2 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell padding="checkbox" style={{ width: '50px' }}>선택</TableCell>
              <TableCell style={{ width: '120px' }}>썸네일</TableCell>
              <TableCell style={{ width: '300px' }}>영상 정보</TableCell>
              <TableCell>성과 지표</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {videos.map((video) => (
              <React.Fragment key={video.url}>
                <TableRow>
                  <TableCell padding="checkbox" rowSpan={2}>
                    <Checkbox
                      checked={selectedVideos[video.url] || false}
                      onChange={() => handleVideoSelect(video.url)}
                    />
                  </TableCell>
                  <TableCell rowSpan={2}>
                    <img 
                      src={video.thumbnail} 
                      alt={video.title}
                      style={{ width: '100px', height: '56px', objectFit: 'cover' }}
                    />
                  </TableCell>
                  <TableCell>
                    <Typography variant="subtitle1" gutterBottom>
                      {video.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      업로드: {video.publishedAt}
                    </Typography>
                  </TableCell>
                  <TableCell>
                    <Box sx={{ display: 'flex', gap: 2 }}>
                      <Chip 
                        label={`조회수: ${video.performance?.views || '미달'}`}
                        color={getPerformanceColor(video.performance?.views)}
                        variant="outlined"
                      />
                      <Chip 
                        label={`좋아요: ${video.performance?.likes || '미달'}`}
                        color={getPerformanceColor(video.performance?.likes)}
                        variant="outlined"
                      />
                      <Chip 
                        label={`댓글: ${video.performance?.comments || '미달'}`}
                        color={getPerformanceColor(video.performance?.comments)}
                        variant="outlined"
                      />
                    </Box>
                  </TableCell>
                </TableRow>
                <TableRow>
                  <TableCell colSpan={2}>
                    <Box sx={{ px: 2, py: 1 }}>
                      {products.map((product) => (
                        <Box key={product.type} sx={{ mb: 2 }}>
                          <Typography variant="body2" gutterBottom>{product.name}</Typography>
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                            <Box sx={{ flex: 1 }}>
                              <Slider
                                disabled={!selectedVideos[video.url]}
                                min={getBaseMetricValue(video.url, product.type)}
                                max={getBaseMetricValue(video.url, product.type) + product.maxAmount}
                                step={product.step}
                                valueLabelDisplay="auto"
                                value={metrics[video.url]?.[product.type] || 0}
                                onChange={(_, value) => 
                                  handleMetricChange(video.url, product.type, value as number)
                                }
                              />
                            </Box>
                            {selectedVideos[video.url] && (
                              <Box sx={{ minWidth: 200 }}>
                                <Typography variant="body2">
                                  증가량: {calculateMetricIncrease(video.url, product.type).toLocaleString()}
                                  {product.type === 'views' ? '회' : '개'}
                                </Typography>
                                <Typography variant="body2">
                                  가격: ₩{calculateMetricPrice(video.url, product.type).toLocaleString()}
                                </Typography>
                              </Box>
                            )}
                          </Box>
                        </Box>
                      ))}
                      {selectedVideos[video.url] && (
                        <Box sx={{ mt: 1, textAlign: 'right' }}>
                          <Typography variant="subtitle2" color="primary">
                            영상 총액: ₩{(
                              calculateMetricPrice(video.url, 'views') +
                              calculateMetricPrice(video.url, 'likes') +
                              calculateMetricPrice(video.url, 'comments')
                            ).toLocaleString()}
                          </Typography>
                        </Box>
                      )}
                    </Box>
                  </TableCell>
                </TableRow>
              </React.Fragment>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Box sx={{ mt: 4, p: 2, bgcolor: 'background.paper' }}>
        <Typography variant="h6" gutterBottom>
          견적서
        </Typography>
        <Typography>
          구독자 늘리기: {subscriberCount.toLocaleString()}명 (₩{(subscriberCount * 1000).toLocaleString()})
        </Typography>
        {Object.entries(selectedVideos).map(([url, isSelected]) => {
          if (isSelected && metrics[url]) {
            const video = videos.find(v => v.url === url);
            const viewsIncrease = calculateMetricIncrease(url, 'views');
            const likesIncrease = calculateMetricIncrease(url, 'likes');
            const commentsIncrease = calculateMetricIncrease(url, 'comments');
            const totalPrice = 
              calculateMetricPrice(url, 'views') +
              calculateMetricPrice(url, 'likes') +
              calculateMetricPrice(url, 'comments');

            return (
              <Box key={url} sx={{ mt: 1 }}>
                <Typography variant="subtitle1">{video?.title}</Typography>
                <Typography variant="body2" sx={{ ml: 2 }}>
                  조회수: +{viewsIncrease.toLocaleString()}회 (₩{calculateMetricPrice(url, 'views').toLocaleString()})
                </Typography>
                <Typography variant="body2" sx={{ ml: 2 }}>
                  좋아요: +{likesIncrease.toLocaleString()}개 (₩{calculateMetricPrice(url, 'likes').toLocaleString()})
                </Typography>
                <Typography variant="body2" sx={{ ml: 2 }}>
                  댓글: +{commentsIncrease.toLocaleString()}개 (₩{calculateMetricPrice(url, 'comments').toLocaleString()})
                </Typography>
                <Typography variant="body2" sx={{ ml: 2 }}>
                  가격: ₩{totalPrice.toLocaleString()}
                </Typography>
              </Box>
            );
          }
          return null;
        })}
        <Typography variant="h6" sx={{ mt: 2 }}>
          총 견적: ₩{calculateTotal().toLocaleString()}
        </Typography>
      </Box>
    </Box>
  );
};
